import { useState } from 'react'
import { useEffect } from 'react'
import Filter from './Filter'
import SuccessNotification from './Notification'
import PersonForm from './PersonForm'
import Persons from './Persons'
import personsService from './services/personsService.js'

const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [searchTerm, setSearchTerm] = useState('')
  const [nextId, setNextId] = useState(1)
  const [successMessage, setSuccessMessage] = useState()

  useEffect(() => {
    personsService
      .getAll()
      .then(initialPersons => {
        setPersons(initialPersons)
        setNextId(persons.length + 1)
      })
  }, [])

  const addPerson = (event) => {
    event.preventDefault()

    const duplicateIndex = persons.findIndex(person => person.name === newName)

    if (duplicateIndex !== -1) {
      if (window.confirm(`${newName} is already added to phonebook, would you like to replace the number?`)) {
        const duplicatePerson = persons[duplicateIndex]
        const updatedPerson = {
          ...duplicatePerson,
          number: newNumber
        }

        personsService
          .update(duplicatePerson.id, updatedPerson)
          .then(() => {
            setPersons(persons.map(person => person.id === duplicatePerson.id ? updatedPerson : person))
        })

        setSuccessMessage(`The number of ${duplicatePerson.name} was successfully updated.`)        
        setTimeout(() => {
          setSuccessMessage(null)
        }, 5000)
      }
    }
    else {
      const personObject = {
        id: nextId,
        name: newName,
        number: newNumber
      }

      setNextId(nextId + 1)

      personsService
        .create(personObject)
        .then(returnedPerson => {
          setPersons(persons.concat(returnedPerson))
      })
    }

    setNewName('')
    setNewNumber('')

    console.log(nextId)
  }

  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    setNewNumber(event.target.value)
  }

  const handleSearchTerm = (event) => {
    setSearchTerm(event.target.value)
  }

  const handleDelete = (event, idForDeletion, nameForDeletion) => {
    if (window.confirm(`Are you sure you want to delete ${nameForDeletion}`)) {
      personsService
          .deletePerson(idForDeletion)
          .then(() => {
            setPersons(persons.filter(person => person.id !== idForDeletion))
      })
    }
  }

  return (
    <div>
      <SuccessNotification message={errorMessage} />
      <h2>Phonebook</h2>
      <Filter searchTerm={searchTerm} handleSearchTerm={handleSearchTerm}/>
      <h3>Add new</h3>
      <PersonForm addPerson={addPerson} newName={newName} newNumber={newNumber} handleNewName={handleNewName} handleNewNumber={handleNewNumber}/>
      <h3>Numbers</h3>
      <Persons persons={persons} searchTerm={searchTerm} handleDelete={handleDelete}/>
    </div>
  )
}

export default App
